            let cthd_LaHH = $.grep(cthd, function (x) {
                return x.LaHangHoa;
            })
            let arrIDQuiDoi = $.unique(cthd_LaHH.map(function (x) {
                return x.ID_DonViQuiDoi;
            }).sort());

            let arrIDLoHang = cthd_LaHH.filter(o => o.QuanLyTheoLoHang).map(function (x) {
                return x.ID_LoHang;
            });

            // idquidoi of combo + tpdl
