    // thứ tự ưu tiên: điểm, thẻ giá trị, pos, chuyển khoản
    function shareMoney_QuyHD(phaiTT, tienDiem, tienmat, tienPOS, chuyenkhoan, thegiatri) {
        if (tienDiem >= phaiTT) {
            return {
                TTBangDiem: phaiTT,
                TienMat: 0,
                TienPOS: 0,
                TienChuyenKhoan: 0,
                TienTheGiaTri: 0,
            }
        }
        else {
            phaiTT = phaiTT - tienDiem;
            if (thegiatri >= phaiTT) {
                return {
                    TTBangDiem: tienDiem,
                    TienMat: 0,
                    TienPOS: 0,
                    TienChuyenKhoan: 0,
                    TienTheGiaTri: Math.abs(phaiTT),
                }
            }
            else {
                phaiTT = phaiTT - thegiatri;
                if (tienPOS >= phaiTT) {
                    return {
                        TTBangDiem: tienDiem,
                        TienMat: 0,
                        TienPOS: Math.abs(phaiTT),
                        TienChuyenKhoan: 0,
                        TienTheGiaTri: thegiatri,
                    }
                }
                else {
                    phaiTT = phaiTT - tienPOS;
                    if (chuyenkhoan >= phaiTT) {
                        return {
                            TTBangDiem: tienDiem,
                            TienMat: 0,
                            TienPOS: tienPOS,
                            TienChuyenKhoan: Math.abs(phaiTT),
                            TienTheGiaTri: thegiatri,
                        }
                    }
                    else {
                        phaiTT = phaiTT - chuyenkhoan;
                        if (tienmat >= phaiTT) {
                            return {
                                TTBangDiem: tienDiem,
                                TienMat: Math.abs(phaiTT),
                                TienPOS: tienPOS,
                                TienChuyenKhoan: chuyenkhoan,
                                TienTheGiaTri: thegiatri,
                            }
                        }
                        else {
                            phaiTT = phaiTT - tienmat;
                            return {
                                TTBangDiem: tienDiem,
                                TienMat: tienmat,
                                TienPOS: tienPOS,
                                TienChuyenKhoan: chuyenkhoan,
                                TienTheGiaTri: thegiatri,
                            }
                        }
                    }
                }
            }
        }
    }

