                    lstCTHD[i].PTChietKhau = ptGiam;
                    lstCTHD[i].TienChietKhau = tienGiam;
                    lstCTHD[i].DVTinhGiam = dvtGiam;
                    lstCTHD[i].ThanhTien = lstCTHD[i].SoLuong * newPrice;
