                    // reset chietkhau, thue
                    itFor.PTChietKhau = 0;
                    itFor.TienChietKhau = 0;
                    itFor.TienThue = 0;
                    itFor.PTThue = 0;
                    itFor.SoLuong = soluong;
                    itFor.DonGia = priceNew;
                    itFor.GiaBan = priceNew;
                    itFor.ThanhToan = sumPrice;
                    itFor.ThanhTien = sumPrice;
                    comboDoing = itFor;

